    }

    function onAdd(input){
        setNotes(prevValue=>{
            return [...prevValue, input];
        })
    }
    

    return <div>
        <Header/>
        <CreateArea add={onAdd} />
        {notes.map((note) => <Note key={note.key} title={note.title} content={note.content} />)}
        <Footer/>
    </div>
}
export default App;
